use IEEE.STD_LOGIC_1164.ALL;
use IEEE.NUMERIC_STD.ALL;

entity opa_controller is
    Port (
        clk : in std_logic;
        reset : in std_logic;
        target_angle : in signed(9 downto 0); -- -90° to +90°, 0.35° resolution
        phase_shifts : out std_logic_vector(511 downto 0) -- 64 ch × 8-bit phase
    );
end opa_controller;

architecture Behavioral of opa_controller is
    signal current_phase : unsigned(7 downto 0) := (others => '0');
begin
    process(clk)
        variable base_delay : integer := 0;
    begin
        if rising_edge(clk) then
            if reset = '1' then
                base_delay := 0;
            else
                base_delay := to_integer(target_angle) * 4; -- Scaling factor
                for i in 0 to 63 loop
                    current_phase <= to_unsigned((base_delay * i) mod 256, 8);
                    phase_shifts((i+1)*8 - 1 downto i*8) <= std_logic_vector(current_phase);
                end loop;
            end if;
        end if;
    end process;
end Behavioral;
```

