% Helmholtz Coil Field Simulator with Misalignment Sensitivity
% Aalto Nanofab - Research Use Only (Project ANF-MAT24)

clear; close all;

% Parameters
r_coil = 0.047; % Coil radius in meters
d_spacing = r_coil; % Helmholtz condition
N_turns = 85;
I_peak = 3.8;
mu0 = 4*pi*1e-7;
f_drive = 50e3;
winding_tol = 0.02; % ±2% misalignment
current_imbalance = 0.05; % ±5%

% Volume of interest: 3 cm diameter spherical volume
[x, y, z] = sphere(30);
x = x * 0.015; y = y * 0.015; z = z * 0.015;

B_total = zeros(size(x));
B_nominal = B_total;

% Nominal field calculation
for i = 1:numel(x)
    B_nominal(i) = compute_biot_savart([x(i), y(i), z(i)], r_coil, d_spacing, N_turns, I_peak, 0, 0);
end

% Perturbed case: worst-case winding offset and current skew
d_offset_pos = d_spacing * (1 + winding_tol);
I_asym = I_peak * (1 + current_imbalance);
B_perturbed = zeros(size(x));
for i = 1:numel(x)
    B_perturbed(i) = compute_biot_savart([x(i), y(i), z(i)], r_coil, d_offset_pos, N_turns, I_asym, 0, 0);
end

% Normalize to 10 mT
B_nominal = B_nominal / max(B_nominal(:)) * 0.01;
B_perturbed = B_perturbed / max(B_perturbed(:)) * 0.01;

% Plot
figure; hold on;
scatter3(x(:), y(:), z(:), 20, abs(B_nominal(:)), 'filled');
colorbar; caxis([0.008 0.01]);
title('Nominal B-Field in DSV (T)');
xlabel('X (m)'); ylabel('Y (m)'); zlabel('Z (m)');
view(3); axis equal; colorbar('Label', 'Flux Density (T)');
light; lighting gouraud;
saveas(gcf, 'h_coil_field_nominal.png');

% Error map
figure;
error_map = abs(B_perturbed - B_nominal);
scatter3(x(:), y(:), z(:), 20, error_map(:), 'filled');
caxis([0 2e-4]);
colorbar('Label', 'Field Deviation (T)');
title('Field Deviation Due to Misalignment & Current Imbalance');
xlabel('X (m)'); ylabel('Y (m)'); zlabel('Z (m)');
view(3); axis equal;
light; lighting gouraud;
saveas(gcf, 'h_coil_error_map.png');

function B = compute_biot_savart(P, R, d, N, I, dx, dy)
    B = [0 0 0];
    dL = 2*pi*R / 100;
    theta = linspace(0, 2*pi, 100);
    current_scale = I;

    for n = 1:2  % Two coils
        z_offset = (n == 1) * (-d/2) + (n == 2) * (d/2);
        coil_x = R * cos(theta);
        coil_y = R * sin(theta);
        coil_z = z_offset * ones(size(theta));

        for k = 1:length(theta)
            r_src = [coil_x(k), coil_y(k), coil_z(k)];
            r_obs = P;
            r_vec = r_obs - r_src;
            r_mag = norm(r_vec);
            if r_mag > 1e-6
                dl = [-sin(theta(k)), cos(theta(k)), 0] * dL;
                dB = (mu0 / (4*pi)) * current_scale * cross(dl, r_vec) / r_mag^3;
                B = B + dB;
            end
        end
    end
    B = norm(B);
end
```

